import React, { useEffect, useReducer } from "react";

export const UserContext = React.createContext();
const userReducer = async(state, action) => {

    // User Logout
    if (action.type === 'USRER_LOGOUT') {
        window.localStorage.clear();
        return {
            ...state,
            login: false
        }
    }

    // Check if user login
    if (action.type === 'CHECK_USER_LOGIN') {
        const jwt = localStorage.getItem('jwt-ktxshop');

        if (jwt === "Ktxshop") {
            return {
                ...state,
                login: true
            }
        } else {
            return {
                ...state,
                login: false
            }
        }
    }

    // User Login
    if (action.type === 'USER_LOGIN') {
        console.log(action.username);
        let data = { props: { data: [] } }
    await fetch(process.env.API_HOST + '/api/orders')
        .then(response => response.json())
        .then(res => {
            data.props.count = res.meta.pagination.total
            res.data.map(item => {
                data.props.data.push({ ...item.attributes, id: item.id })
            })
        });
        return {
            ...state,
            login: true
        }
        // if (getToken == token) {
        //     return {
        //         ...state,
        //         login: true
        //     }
        // } else {
        //     return {
        //         ...state,
        //         login: false
        //     }
        // }

    }

}
export const UserProvider = ({ children }) => {
    const [user, dispatchUser] = useReducer(userReducer, {
        login: false
    })
    const userLogin = (username, password) => {
        dispatchUser({ type: 'USER_LOGIN', username: username, password: password })
    }

    useEffect(() => {
        dispatchUser({ type: 'CHECK_USER_LOGIN' })
    }, [])

    return (
        <UserContext.Provider
            value={{
                login: user.login,
                userLogin: userLogin,
            }}>
            {children}
        </UserContext.Provider>
    );

}
