        return (
            <React.Fragment>
                <div className="order-btn">
                    <button disabled={this.props.disabled} className={`default-btn`} onClick={() => this.handlePayment(this.props.userData, this.props.stateSchema)}>
                        Đặt hàng
                    </button>
                </div>
            </React.Fragment>
        );
    }
}


