import React, {useContext} from 'react';
import TopHeader from '../components/Layouts/TopHeader';
import Navbar from '../components/Layouts/Navbar';
import PageBanner from '../components/Common/PageBanner';
import { useRouter } from 'next/router';
import ProductDetailsStyleOne from '../components/ProductDetails/ProductDetailsStyleOne';
import FacilitySlider from '../components/Common/FacilitySlider';
import Footer from '../components/Layouts/Footer';
import { UserContext } from '../contexts/UserContext';
import CartContext from '../contexts/CartContext';

const Product = ({product}) => {
    const user = useContext(UserContext);
    const cart = useContext(CartContext);
    return (
        <React.Fragment>
            <TopHeader user={user}/>
            <Navbar />
            <PageBanner 
                pageTitle="Chi tiết sản phẩm" 
                homePageUrl="/" 
                homePageText="Trang chủ" 
                activePageText="" 
            /> 
            <ProductDetailsStyleOne product={product} />
            <FacilitySlider />
            <Footer />
        </React.Fragment>
    );
}

export async function getServerSideProps(ctx) {
    let data = {
        props: {
            data: [],
        }
    };
    const router = useRouter();
    let { id } = this.props.router.query;
    await fetch(process.env.API_HOST + '/api/products/' + id)
        .then(response => response.json())
        .then(res => {
            data.props.data.push(res.data.attributes)
        });
    return data;
}
export default Product;
