import React, { useEffect, useReducer } from "react";
let account;
export const UserContext = React.createContext();
const userReducer = (state, action) => {

    // User Logout
    if (action.type === 'USRER_LOGOUT') {
        window.localStorage.clear();
        return {
            ...state,
            login: false
        }
    }

    // Check if load page
    if (action.type === 'CHECK_USER_LOGIN') {
        const jwt = localStorage.getItem('jwt-ktxshop');

        if (jwt === "Ktxshop") {
            return {
                ...state,
                login: true
            }
        } else {
            return {
                ...state,
                login: false
            }
        }
    }

    // User Login
    if (action.type === 'USER_LOGIN') {
        localStorage.setItem('jwt-ktxshop', action.payload.data);
        if(action.account){
            return {
                ...state,
                account: action.account,
                login: true
            }
        }else{
            return {
                ...state,
                login: false
            }
        }
    }

}

export const UserProvider = ({ children }) => {
    const [user, dispatchUser] = useReducer(userReducer, {
        account: {},
        login: false
    })
    const userLogin = (account) => {
        dispatchUser({ type: 'USER_LOGIN', account })
    }

    useEffect(() => {
        dispatchUser({ type: 'CHECK_USER_LOGIN' })
    }, [])

    return (
        <UserContext.Provider
            value={{
                login: user.login,
                account: user.account,
                userLogin: userLogin,
            }}>
            {children}
        </UserContext.Provider>
    );

}
