import React, { useEffect, useReducer } from "react";

export const UserContext = React.createContext();
const userReducer = async(state, action) => {

    // User Logout
    if (action.type === 'USRER_LOGOUT') {
        window.localStorage.clear();
        return {
            ...state,
            login: false
        }
    }

    // Check if user login
    if (action.type === 'CHECK_USER_LOGIN') {
        const jwt = localStorage.getItem('jwt-ktxshop');

        if (jwt === "Ktxshop") {
            return {
                ...state,
                login: true
            }
        } else {
            return {
                ...state,
                login: false
            }
        }
    }

    // User Login
    if (action.type === 'USER_LOGIN') {
        console.log(action.username);
        var myHeaders = new Headers();
        myHeaders.append("Content-Type", "application/json")
        await fetch(process.env.API_HOST + '/api/products', {
            method: 'POST',
            body: JSON.stringify({
                data: {
                    name: name,
                    price: price,
                    imageUrl: imgUrl
                }
            }),
            headers: myHeaders
        })
            .then(response => response.json())
            .then(result => {
                router.push("/cms/product")
            })

        return {
            ...state,
            login: true
        }
        // if (getToken == token) {
        //     return {
        //         ...state,
        //         login: true
        //     }
        // } else {
        //     return {
        //         ...state,
        //         login: false
        //     }
        // }

    }

}
export const UserProvider = ({ children }) => {
    const [user, dispatchUser] = useReducer(userReducer, {
        login: false
    })
    const userLogin = (username, password) => {
        dispatchUser({ type: 'USER_LOGIN', username: username, password: password })
    }

    useEffect(() => {
        dispatchUser({ type: 'CHECK_USER_LOGIN' })
    }, [])

    return (
        <UserContext.Provider
            value={{
                login: user.login,
                userLogin: userLogin,
            }}>
            {children}
        </UserContext.Provider>
    );

}
