import React, { Component, useState } from "react";

import CartContext from "../../contexts/CartContext";

export const CartProvider = () => {
    const [number, setNumber] = useState(0);

    const updateNumber = () => {
        this.setState({
            number: Math.random()
        });
    }

    
        return (
            <CartContext.Provider
                value={{
                    number: number,
                    updateNumber: updateNumber
                }}
            >
                {this.props.children}
            </CartContext.Provider>
        );
    
}
