import React, { Component } from "react";

import CartContext from "../../contexts/CartContext";

export const CartProvider = () => {
    

    const updateNumber = () => {
        this.setState({
            number: Math.random()
        });
    }

    
        return (
            <CartContext.Provider
                value={{
                    number: this.state.number,
                    updateNumber: this.updateNumber
                }}
            >
                {this.props.children}
            </CartContext.Provider>
        );
    
}
