import React, { useEffect, useReducer } from "react";

export const UserContext = React.createContext();
const userReducer = async (state, action) => {

    // User Logout
    if (action.type === 'USRER_LOGOUT') {
        window.localStorage.clear();
        return {
            ...state,
            login: false
        }
    }

    // Check if user login
    if (action.type === 'CHECK_USER_LOGIN') {
        const jwt = localStorage.getItem('jwt-ktxshop');

        if (jwt === "Ktxshop") {
            return {
                ...state,
                login: true
            }
        } else {
            return {
                ...state,
                login: false
            }
        }
    }

    // User Login
    if (action.type === 'USER_LOGIN') {
        const qs = require('qs');
        const query = qs.stringify({
            filters: {
              UserName: {
                $eq: action.username,
              },
            },
          }, {
            encodeValuesOnly: true,
          });
        let data;
        await fetch(process.env.API_HOST + `/api/accounts?${query}`)
            .then(response => response.json())
            .then(res => {
               if(res.data){
                   data = res.data[0].attributes;
               }
            });
        if(data.UserName === action.username){
            return {
                ...state,
                login: true
            }
        }else{
            return {
                ...state,
                login: false
            }
        }
       
        // if (getToken == token) {
        //     return {
        //         ...state,
        //         login: true
        //     }
        // } else {
        //     return {
        //         ...state,
        //         login: false
        //     }
        // }

    }

}
export const UserProvider = ({ children }) => {
    const [user, dispatchUser] = useReducer(userReducer, {
        login: false
    })
    const userLogin = (username, password) => {
        dispatchUser({ type: 'USER_LOGIN', username: username, password: password })
    }

    useEffect(() => {
        dispatchUser({ type: 'CHECK_USER_LOGIN' })
    }, [])

    return (
        <UserContext.Provider
            value={{
                login: user.login,
                userLogin: userLogin,
            }}>
            {children}
        </UserContext.Provider>
    );

}
