import React, { Component } from 'react';
import { toast } from 'react-toastify';
import Link from 'next/link';
import Payment from '../payments/Payment';
class CheckoutForm extends Component {
    handleEmptyForm = () => {
        toast.error('Please fillup the form carefully', {
            position: "bottom-left",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: true
        });
    }
    
    render() {
        let totalAmount = (this.props.total + this.props.shipping).toFixed(2)
        let { disabled,stateData } = this.props;
        return (
            <div className="col-lg-6 col-md-12">
                <div className="order-details">
                    <h3 className="title">Đơn hàng của bạn</h3>

                    <div className="order-table table-responsive">
                        <table className="table table-bordered">
                            <thead>
                                <tr>
                                    <th scope="col">Tên sản phẩm</th>
                                    <th scope="col">Total</th>
                                </tr>
                            </thead>

                            <tbody>
                                {stateData.cart.addedItems.map((data, idx) => (
                                    <tr key={idx}>
                                        <td className="product-name">
                                            <a href="#">{data.title}</a>
                                        </td>

                                        <td className="product-total">
                                            <span className="subtotal-amount">${data.newPrice * data.quantity}</span>
                                        </td>
                                    </tr>
                                ))}
                                
                                <tr>
                                    <td className="order-subtotal">
                                        <span>Tổng tiền giỏ hàng</span>
                                    </td>

                                    <td className="order-subtotal-price">
                                        <span className="order-subtotal-amount">{stateData.cart.total} đ</span>
                                    </td>
                                </tr>

                                {/* <tr>
                                    <td className="order-shipping">
                                        <span>Shipping</span>
                                    </td>

                                    <td className="shipping-price">
                                        <span>$30</span>
                                    </td>
                                </tr> */}
                                <tr>
                                    <td className="total-price">
                                        <span>Tổng tiền phải thanh toán</span>
                                    </td>

                                    <td className="product-subtotal">
                                        <span className="subtotal-amount">{stateData.cart.total} đ</span>
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>

                    <div className="payment-box">
                        <div className="payment-method">
                            <p>
                                <input type="radio" id="direct-bank-transfer" name="radio-group" defaultChecked />
                                <label>Direct Order</label>
                                Make your payment directly into our bank account. Please use your Order ID as the payment reference. Your order will not be shipped until the funds have cleared in our account.
                            </p>
                        </div>

                        {
                            disabled ? (
                                <Link href="#">
                                    <a 
                                        className={disabled ? 'default-btn disabled-btn' : 'default-btn'}
                                        onClick={e => {
                                            e.preventDefault();
                                            this.handleEmptyForm();
                                        }}
                                    >Place Order</a>
                                </Link>
                            ) : (
                                <Payment 
                                    amount={totalAmount * 100}
                                    userData={this.props.stateData}
                                    disabled={disabled}
                                />
                            )
                        }
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        products: state.addedItems,
        total: state.total,
        shipping: state.shipping
    }
}

export default (CheckoutForm)
