import React, { Component, useState } from "react";

import CartContext from "../../contexts/CartContext";

export const CartProvider = ({children}) => {
    const [number, setNumber] = useState(2);

    const updateNumber = () => {
        setNumber({
            number: Math.random()
        });
    }
    return (
        <CartContext.Provider
            value={{
                number: number,
                updateNumber: updateNumber
            }}
        >
            {children}
        </CartContext.Provider>
    );

}
