import React, { Component, useContext } from 'react';
import Link from 'next/link';
import Router from 'next/router';
import TopHeader from '../components/Layouts/TopHeader';
import Navbar from '../components/Layouts/Navbar';
import PageBanner from '../components/Common/PageBanner';
import FacilitySlider from '../components/Common/FacilitySlider';
import Footer from '../components/Layouts/Footer';
import { UserContext } from '../contexts/UserContext';
const Register = () => {
    const user = useContext(UserContext);
    const handleRegister = async (e) => {
        e.preventdefault();
        e.preventDefault();
        const account = {
            username: e.target.elements.username.value,
            password: e.target.elements.password.value,
        }
        let data;
        const qs = require('qs');
        const query = qs.stringify({
            filters: {
                UserName: {
                    $eq: account.username,
                },
            },
        }, {
            encodeValuesOnly: true,
        });
        await fetch(process.env.API_HOST + `/api/accounts?${query}`)
            .then(response => response.json())
            .then(res => {
                if (res.data && res.data[0] && res.data[0].attributes) {
                    data = res.data[0];
                }
            });
        if (!data) {
            Swal.fire({
                icon: 'error',
                title: 'Đăng nhập không thành công',
                text: 'Username hoặc password không hợp lệ',
            });

            return;
        }
    }

    return (
        <React.Fragment>
            <TopHeader user={user} />
            <Navbar />
            <PageBanner
                pageTitle="Tài khoản"
                homePageUrl="/"
                homePageText="Trang chủ"
                activePageText="Đăng ký"
            />

            <section className="signup-area ptb-100">
                <div className="container">
                    <div className="signup-content">
                        <h2>Tạo tài khoản</h2>

                        <form onSubmit={(e) =>handleRegister(e)} className="signup-form">

                            <div className="form-group">
                                <label>Username</label>
                                <input type="email" className="form-control" id="username" name="username" />
                            </div>

                            <div className="form-group">
                                <label>Password</label>
                                <input type="password" className="form-control" id="password" name="password" />
                            </div>

                            <button type="submit" className="default-btn">Đăng ký</button>

                            <div className="text-center">
                                <Link href="/">
                                    <a className="return-store">Trở lại trang chủ</a>
                                </Link>
                            </div>
                        </form>
                    </div>
                </div>
            </section>

            <FacilitySlider />
            <Footer />
        </React.Fragment>
    );

}



export default (Register)
